resource "aws_eip" "nat" {
  domain = "vpc"
  tags = merge(
    {
      "Name" = format("%s-nat-eip", var.vpc_name)
    },
    var.tags,
  )

  depends_on = [aws_internet_gateway.this]
}

resource "aws_vpc" "main" {
